import { Outlet, useNavigate } from "react-router-dom"
import "./Pitch.css"
import { useEffect, useRef } from "react"

const onKeyDown = (e, nav, opts) => {
  switch (e.key) {
    case "Enter": {nav(opts.next); break;}
    case "ArrowRight": {nav(opts.next); break;}
    case "ArrowLeft": {nav(opts.back, {end: true}); break}
  }
}

const useFocusedRender = () => {
  const ref = useRef();
  useEffect(() => {
    ref.current?.focus();
  }, [ref])

  return ref;
}

const Pitch = function Pitch() {

  return (
    <div className="pitch-index">
      <div className="topbar">
        <span>sprive</span>
        <span className="thrive">helping all life to thrive</span>
      </div>
      <Outlet />
    </div>
  )
}

export default Pitch

